function AverageDelay = Evaluate(NeighbourSolution)    Matrizes;    miu = R * 1e9 / (8 * 1000);    lambda_s = T * 1e6 / (8 * 1000);    gama = sum(sum(lambda_s));    d = L * 1e3 / 2e8;    lambda = NeighbourSolution.lambda;    %Calculate average delay    AverageDelay = (lambda./(miu-lambda) + lambda.*d);    AverageDelay(isnan(AverageDelay)) = 0;    AverageDelay = 2*sum(sum(AverageDelay)) / gama;end